import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import styles from './SellPremium.module.css'; // Ensure this CSS module exists

const SellPremium = () => {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState(''); // Add price state
    const [file, setFile] = useState(null);
    const [message, setMessage] = useState('');
    const [username, setUsername] = useState(''); // Add state for username
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUsername = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/profile', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}` // Include the token in the request
                    }
                }); // Adjust the URL to your profile endpoint
                setUsername(res.data.username);
            } catch (err) {
                console.error('Error fetching profile:', err);
                setMessage('Error fetching profile');
            }
        };
        fetchUsername();
    }, []);

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('name', name);
        formData.append('description', description);
        formData.append('price', price); // Add price to formData
        formData.append('file', file);
        formData.append('username', username); // Use fetched username
        formData.append('isPremium', true); // Mark the product as premium

        try {
            const res = await axios.post('http://localhost:5000/api/products/premium', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    Authorization: `Bearer ${localStorage.getItem('token')}` // Include the token in the request
                }
            });
            setMessage('Premium product listed successfully');
            setName('');
            setDescription('');
            setPrice(''); // Reset price
            setFile(null);
            console.log('Premium product listed successfully:', res.data);
        } catch (err) {
            console.error('Error listing premium product:', err);
            setMessage('Error listing premium product');
        }
    };

    return (
        <div className={styles.container}>
            <h2>Sell Your Premium Product</h2>
            {message && <p>{message}</p>}
            <form onSubmit={handleSubmit} className={styles.form}>
                <div>
                    <label htmlFor="name">Product Name</label>
                    <input
                        type="text"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="description">Product Description</label>
                    <textarea
                        id="description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        required
                    ></textarea>
                </div>
                <div>
                    <label htmlFor="price">Product Price</label>
                    <input
                        type="number"
                        id="price"
                        value={price}
                        onChange={(e) => setPrice(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="file">Upload File</label>
                    <input
                        type="file"
                        id="file"
                        onChange={handleFileChange}
                        accept=".zip,.rar,.7z,.doc,.docx,.pdf"
                        required
                    />
                </div>
                <button type="submit">List Premium Product</button>
            </form>
        </div>
    );
};

export default SellPremium;
