import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styles from './BrowseProducts.module.css'; // Custom CSS module
import Navigation from './Navigation';

const BrowseProducts = ({ username }) => {
    const [products, setProducts] = useState([]);
    const [search, setSearch] = useState('');
    const [message, setMessage] = useState('');

    // Fetch all products on component mount
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/products');
                if (Array.isArray(res.data)) {
                    setProducts(res.data);
                } else {
                    setMessage('Invalid product data');
                    console.error('Unexpected response data:', res.data);
                }
            } catch (err) {
                console.error('Error fetching products:', err);
                setMessage('Failed to load products');
            }
        };
        fetchProducts();
    }, []);

    // Handle like/unlike toggle
    const handleLike = async (productId) => {
        try {
            const res = await axios.post(`http://localhost:5000/api/products/${encodeURIComponent(productId)}/like`, { username });
            const updated = res.data;
            if (updated && typeof updated.likes === 'number') {
                setProducts(products.map(product =>
                    product._id === productId
                        ? { ...product, likes: updated.likes, likedBy: updated.likedBy }
                        : product
                ));
            }
        } catch (err) {
            console.error('Error toggling like:', err);
            setMessage('Could not like product');
        }
    };

    // Handle new comment
    const handleComment = async (productId, comment) => {
        try {
            const res = await axios.post(`http://localhost:5000/api/products/${encodeURIComponent(productId)}/comment`, { comment });
            if (Array.isArray(res.data.comments)) {
                setProducts(products.map(product =>
                    product._id === productId
                        ? { ...product, comments: res.data.comments }
                        : product
                ));
            }
        } catch (err) {
            console.error('Error posting comment:', err);
            setMessage('Failed to comment');
        }
    };

    // Filter products by search
    const filteredProducts = products.filter(product =>
        product.name.toLowerCase().includes(search.toLowerCase())
    );

    return (
        <div className={styles.container}>
            <Navigation />
            <h2 className={styles.title}>Browse Our Products</h2>

            {message && <p className={styles.error}>{message}</p>}

            <input
                type="text"
                placeholder="Search products..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                className={styles.searchInput}
            />

            <div className={styles.productGrid}>
                {filteredProducts.length > 0 ? (
                    filteredProducts.map(product => (
                        <div key={product._id} className={styles.productCard}>
                            <h3 className={styles.productName}>{product.name}</h3>
                            <p className={styles.productDescription}>{product.description}</p>

                            <a
                                className={styles.downloadBtn}
                                href={`http://localhost:5000/api/products/files/${product.fileUrl.split('/').pop()}`}
                                download
                            >
                                Download File
                            </a>

                            <button
                                className={styles.likeBtn}
                                onClick={() => handleLike(product._id)}
                            >
                                {product.likedBy.includes(username) ? '❤️ Unlike' : '🤍 Like'} ({product.likes})
                            </button>

                            <div className={styles.commentsSection}>
                                <h4>Comments</h4>
                                {product.comments && product.comments.length > 0 ? (
                                    product.comments.map((comment, index) => (
                                        <p key={index} className={styles.commentItem}>💬 {comment}</p>
                                    ))
                                ) : (
                                    <p>No comments yet.</p>
                                )}
                                <form
                                    onSubmit={(e) => {
                                        e.preventDefault();
                                        handleComment(product._id, e.target.comment.value);
                                        e.target.comment.value = '';
                                    }}
                                    className={styles.commentForm}
                                >
                                    <input
                                        type="text"
                                        name="comment"
                                        placeholder="Add a comment..."
                                        required
                                        className={styles.commentInput}
                                    />
                                    <button type="submit" className={styles.commentButton}>Post</button>
                                </form>
                            </div>
                        </div>
                    ))
                ) : (
                    <p className={styles.noProducts}>No products found</p>
                )}
            </div>
        </div>
    );
};

export default BrowseProducts;
