import React, { useState } from 'react';
import axios from 'axios';
import styles from './Sell.module.css'; // Create CSS module for styling

const Sell = () => {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [file, setFile] = useState(null);
    const [message, setMessage] = useState('');

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('name', name);
        formData.append('description', description);
        formData.append('file', file);

        try {
            const res = await axios.post('http://localhost:5000/api/products', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            setMessage('Product listed successfully');
            setName('');
            setDescription('');
            setFile(null);
            console.log('Product listed successfully:', res.data);
        } catch (err) {
            console.error('Error listing product:', err);
            setMessage('Error listing product');
        }
    };

    return (
        <div className={styles.container}>
            <h2>Sell Your Product</h2>
            {message && <p>{message}</p>}
            <form onSubmit={handleSubmit} className={styles.form}>
                <div>
                    <label htmlFor="name">Product Name</label>
                    <input
                        type="text"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="description">Product Description</label>
                    <textarea
                        id="description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        required
                    ></textarea>
                </div>
                <div>
                    <label htmlFor="file">Upload ZIP File or Document</label>
                    <input
                        type="file"
                        id="file"
                        onChange={handleFileChange}
                        accept=".zip,.rar,.7z,.doc,.docx,.pdf"
                        required
                    />
                </div>
                <button type="submit">List Product</button>
            </form>
        </div>
    );
};

export default Sell;
