import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navigation from './Navigation';
import { FaHeart, FaRegHeart, FaDownload, FaComment } from 'react-icons/fa';

const BrowseProducts = ({ username }) => {
  const [products, setProducts] = useState([]);
  const [search, setSearch] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/products');
        if (Array.isArray(res.data)) {
          setProducts(res.data);
        } else {
          console.error('Unexpected response:', res.data);
        }
      } catch (err) {
        console.error('Error fetching products:', err);
        setMessage('Error loading products.');
      }
    };

    fetchProducts();
  }, []);

  const handleLike = async (productId) => {
    try {
      const res = await axios.post(`http://localhost:5000/api/products/${encodeURIComponent(productId)}/like`, { username });
      if (res.data && typeof res.data.likes === 'number') {
        setProducts(products.map(product =>
          product._id === productId
            ? { ...product, likes: res.data.likes, likedBy: res.data.likedBy }
            : product
        ));
      }
    } catch (err) {
      console.error('Like error:', err);
      setMessage('Failed to like product.');
    }
  };

  const handleComment = async (productId, comment) => {
    try {
      const res = await axios.post(`http://localhost:5000/api/products/${encodeURIComponent(productId)}/comment`, { comment });
      if (res.data && Array.isArray(res.data.comments)) {
        setProducts(products.map(product =>
          product._id === productId
            ? { ...product, comments: res.data.comments }
            : product
        ));
      }
    } catch (err) {
      console.error('Comment error:', err);
      setMessage('Failed to post comment.');
    }
  };

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gray-100">
      <Navigation />
      <div className="max-w-6xl mx-auto px-4 py-10">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">Browse Products</h2>
        {message && <p className="text-center text-red-500">{message}</p>}

        <input
          type="text"
          placeholder="Search products..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="w-full p-3 rounded-xl mb-6 border border-gray-300 shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
        />

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredProducts.length > 0 ? (
            filteredProducts.map((product) => (
              <div key={product._id} className="bg-white rounded-2xl p-5 shadow hover:shadow-lg flex flex-col justify-between">
                <div>
                  <h3 className="text-lg font-semibold text-gray-900">{product.name}</h3>
                  <p className="text-sm text-gray-700 mt-1 mb-3">{product.description}</p>

                  <a
                    href={`http://localhost:5000/api/products/files/${product.fileUrl.split('/').pop()}`}
                    className="text-blue-600 hover:underline text-sm inline-flex items-center gap-1"
                    download
                  >
                    <FaDownload /> Download File
                  </a>
                </div>

                <div className="mt-4 flex items-center justify-between">
                  <button
                    onClick={() => handleLike(product._id)}
                    className="flex items-center gap-1 text-red-500 hover:text-red-600"
                  >
                    {product.likedBy.includes(username) ? <FaHeart /> : <FaRegHeart />}
                    <span className="text-sm">{product.likes}</span>
                  </button>
                </div>

                <div className="mt-4">
                  <h4 className="font-semibold text-sm text-gray-700 mb-1">Comments:</h4>
                  <div className="max-h-28 overflow-y-auto text-sm text-gray-600 space-y-1">
                    {product.comments && product.comments.length > 0 ? (
                      product.comments.map((comment, index) => (
                        <p key={index}>- {comment}</p>
                      ))
                    ) : (
                      <p>No comments yet.</p>
                    )}
                  </div>

                  <form
                    onSubmit={(e) => {
                      e.preventDefault();
                      handleComment(product._id, e.target.comment.value);
                      e.target.comment.value = '';
                    }}
                    className="mt-3 flex gap-2"
                  >
                    <input
                      type="text"
                      name="comment"
                      placeholder="Add a comment..."
                      className="flex-1 px-3 py-2 border rounded-lg text-sm"
                      required
                    />
                    <button
                      type="submit"
                      className="px-3 py-2 bg-blue-500 text-white rounded-lg text-sm hover:bg-blue-600"
                    >
                      <FaComment />
                    </button>
                  </form>
                </div>
              </div>
            ))
          ) : (
            <p className="text-center text-gray-500 col-span-full">No products found.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default BrowseProducts;
