import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import Navigation from './Navigation';
import './Dashboard.css';

const Dashboard = ({ handleLogout }) => {
    const [user, setUser] = useState(null); // Stores the user details
    const [loading, setLoading] = useState(true); // Tracks loading state
    const [error, setError] = useState(null); // Tracks error state
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('You are not logged in. Redirecting to login...');
                    setTimeout(() => navigate('/login'), 2000);
                    return;
                }

                const res = await axios.get('https://mern-project-5-xoai.onrender.com/api/auth/dashboard', {
                    headers: { Authorization: `Bearer ${token}` },
                });

                setUser(res.data.user);
                setError(null);
            } catch (err) {
                console.error('Error fetching user data:', err);
                setError('Failed to fetch user data. Please try again.');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [navigate]);

    return (
        <div className="dashboard-container">
            {/* Navigation */}
            <Navigation handleLogout={handleLogout} />

            <div className="content">
                {user ? <p>Welcome, {user.name}!</p> : <p>Loadi</p>}
            </div>

            {/* User Greeting or Loading/Error State */}
            <div className="content">
                {loading ? (
                    <p>Loading your dashboard...</p>
                ) : error ? (
                    <p className="error-message">{error}</p>
                ) : user ? (
                    <p>Welcome back, <strong>{user.name}</strong>!</p>
                ) : (
                    <p>Welcome! Please log in to access your dashboard.</p>
                )}
            </div>

            {/* Action Buttons */}
            <div className="buttons-container">
                <Link to="/browse-products" className="button">
                    Browse Products
                </Link>
                <Link to="/sell" className="button">
                    Become a Seller
                </Link>
            </div>

            {/* Placeholder for Future Content */}
            <div className="additional-content">
                <h2>Your Dashboard</h2>
                <p>Explore offers, track your activity, and more!</p>
                {/* Additional sections or components can be added here */}
            </div>
        </div>
    );
};

export default Dashboard;
