import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import Navigation from './Navigation';
import './Dashboard.css';
import ImageSlider from './ImageSlider'; // Import the new ImageSlider component

const Dashboard = ({ handleLogout }) => {
    const [user, setUser] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const token = localStorage.getItem('token');
                if (token) {
                    const res = await axios.get('http://localhost:5000/api/auth/dashboard', {
                        headers: { 'Authorization': `Bearer ${token}` }
                    });
                    setUser(res.data.user);
                }
            } catch (err) {
                console.error(err);
            }
        };
        
        const preloadImages = (imageArray, callback) => {
            let loadedImages = 0;
            imageArray.forEach(src => {
                const img = new Image();
                img.src = src;
                img.onload = () => {
                    loadedImages++;
                    if (loadedImages === imageArray.length) {
                        callback();
                    }
                };
            });
        };

        preloadImages([
            require('../assets/image1.jpg').default,
            require('../assets/image2.jpg').default,
            require('../assets/image3.jpg').default,
            require('../assets/image4.jpg').default
        ], () => setIsLoaded(true));

        fetchData();
    }, []);

    return (
        <div className="dashboard-container">
            <Navigation handleLogout={handleLogout} />
            
            <div className="content">
                {user ? <p>Welcome, {user.name}!</p> : <p>Loading...</p>}
            </div>

            {isLoaded && <ImageSlider />} 

            <div className="buttons-container">
                <Link to="/browse-products" className="button">
                    Browse Products
                </Link>
                <Link to="/sell" className="button">
                    Become a Seller
                </Link>
            </div>
        </div>
    );
};

export default Dashboard;
