import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import Navigation from './Navigation';
import './Dashboard.css'; // Ensure to import CSS
import Slider from 'react-slick'; // Import Slider
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css"; 
import './ImageSlider.css'; // Import the new CSS file for the slider

const Dashboard = ({ handleLogout }) => {
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const token = localStorage.getItem('token');
                if (token) {
                    const res = await axios.get('http://localhost:5000/api/auth/dashboard', {
                        headers: { 'Authorization': `Bearer ${token}` }
                    });
                    setUser(res.data.user);
                }
            } catch (err) {
                console.error(err);
            }
        };
        fetchData();
    }, []);

    const images = [
        require('../assets/ima').default,
        require('../assets/ima').default,
        require('../assets/ima').default
    ];

    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000
    };

    return (
        <div className="dashboard-container">
            <Navigation handleLogout={handleLogout} />
            
            <div className="content">
                {user ? <p>Welcome, {user.name}!</p> : <p>Loading...</p>}
            </div>

            <Slider {...settings} className="image-slider">
                {images.map((src, index) => (
                    <div key={index}>
                        <img src={src} alt={`Slide ${index + 1}`} className="slider-image" />
                    </div>
                ))}
            </Slider>

            <div className="buttons-container">
                <Link to="/browse-products" className="button">
                    Browse Products
                </Link>
                <Link to="/sell" className="button">
                    Become a Seller
                </Link>
            </div>
        </div>
    );
};

export default Dashboard;
